"use client"

import { useState, useEffect, useCallback, useRef } from "react"
import { ChevronDown, ChevronRight, Plus, Check, MoreVertical, Trash, MoveVertical } from "lucide-react"
import api from "../services/api"
import { toast } from "react-toastify"
import MoveTaskModal from "./MoveTaskModal"
import "./TaskItem.css"

const TaskItem = ({ task, onStatusChange, onCreateSubtask, depth, fetchTasks, lists }) => {
  const [expanded, setExpanded] = useState(true)
  const [showMenu, setShowMenu] = useState(false)
  const [isMoveModalOpen, setIsMoveModalOpen] = useState(false)
  const [allSubtasksCompleted, setAllSubtasksCompleted] = useState(false)
  const menuRef = useRef(null)

  const maxDepth = 2 // 0-based index, so this is 3 levels
  const canHaveChildren = depth < maxDepth
  const isTopLevel = depth === 0

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setShowMenu(false)
      }
    }

    if (showMenu) {
      document.addEventListener("mousedown", handleClickOutside)
    }

    return () => {
      document.removeEventListener("mousedown", handleClickOutside)
    }
  }, [showMenu])

  // The handleStatusChange function needs to be memoized with useCallback to avoid infinite loops
  const handleStatusChange = useCallback(
    async (isAutoComplete = false) => {
      // If trying to mark as incomplete but has completed subtasks, show error
      if (task.status === "Done" && allSubtasksCompleted && !isAutoComplete) {
        toast.error("Cannot mark task as incomplete while it has completed subtasks", { autoClose: 5000 })
        return
      }

      // If trying to mark as complete but has incomplete subtasks, show error
      if (
        task.status !== "Done" &&
        task.subtasks &&
        task.subtasks.length > 0 &&
        !allSubtasksCompleted &&
        !isAutoComplete
      ) {
        toast.error("Cannot mark task as complete until all subtasks are completed", { autoClose: 5000 })
        return
      }

      const newStatus = task.status === "Done" ? "Todo" : "Done"
      try {
        await api.patch(`/api/todos/task/${task.id}/status`, { status: newStatus })

        if (!isAutoComplete) {
          toast.success(`Task marked as ${newStatus}`)
        }

        onStatusChange(task.id, newStatus)

        // If marking as complete and this is a subtask, check if all siblings are complete
        if (newStatus === "Done" && depth > 0) {
          fetchTasks() // Refresh to trigger parent task completion check
        }
      } catch (error) {
        toast.error("Failed to update task status", { autoClose: 5000 })
      }
    },
    [task.status, task.id, task.subtasks, allSubtasksCompleted, depth, onStatusChange, fetchTasks],
  )

  // Check if all subtasks are completed
  useEffect(() => {
    if (task.subtasks && task.subtasks.length > 0) {
      const allCompleted = task.subtasks.every((subtask) => subtask.status === "Done")
      setAllSubtasksCompleted(allCompleted)

      // If all subtasks are completed and the task is not completed, mark it as completed
      if (allCompleted && task.status !== "Done") {
        handleStatusChange(true)
      }
    }
  }, [task.subtasks, task.status, handleStatusChange])

  const handleToggleExpand = () => {
    setExpanded(!expanded)
  }

  const handleDeleteTask = async () => {
    try {
      await api.delete(`/api/todos/task/${task.id}`)
      toast.success("Task deleted successfully")
      fetchTasks()
    } catch (error) {
      toast.error("Failed to delete task", { autoClose: 5000 })
    }
  }

  const handleMoveTask = async (destinationListId) => {
    try {
      await api.patch(`/api/todos/task/${task.id}/move`, { list_id: destinationListId })
      toast.success("Task moved successfully")
      fetchTasks()
      setIsMoveModalOpen(false)
    } catch (error) {
      toast.error("Failed to move task", { autoClose: 5000 })
    }
  }

  const getStatusClass = (status) => {
    switch (status) {
      case "Done":
        return "status-done"
      case "In Progress":
        return "status-progress"
      default:
        return "status-todo"
    }
  }

  return (
    <div className={`task-item ${task.status === "Done" ? "completed" : ""}`} style={{ marginLeft: `${depth * 20}px` }}>
      <div className="task-header">
        <div className="task-expand">
          {task.subtasks && task.subtasks.length > 0 ? (
            <button onClick={handleToggleExpand} className="expand-button">
              {expanded ? <ChevronDown size={18} /> : <ChevronRight size={18} />}
            </button>
          ) : (
            <div className="expand-placeholder"></div>
          )}
        </div>

        <div className="task-content">
          <div className="task-main">
            <div className="task-checkbox-container">
              <button
                onClick={() => handleStatusChange()}
                className={`task-checkbox ${task.status === "Done" ? "checked" : ""}`}
                aria-label={task.status === "Done" ? "Mark as incomplete" : "Mark as complete"}
              >
                {task.status === "Done" && <Check size={14} className="check-icon" />}
              </button>

              <div className="task-details">
                <h3 className={`task-title ${task.status === "Done" ? "completed" : ""}`}>{task.title}</h3>
                {task.description && <p className="task-description">{task.description}</p>}
                <div className="task-meta">
                  <span className={`task-status ${getStatusClass(task.status)}`}>{task.status}</span>
                </div>
              </div>
            </div>

            <div className="task-actions" ref={menuRef}>
              <div className="dropdown">
                <button onClick={() => setShowMenu(!showMenu)} className="dropdown-button" aria-label="Task options">
                  <MoreVertical size={18} />
                </button>

                {showMenu && (
                  <div className="dropdown-menu">
                    {canHaveChildren && (
                      <button
                        onClick={() => {
                          setShowMenu(false)
                          onCreateSubtask(task)
                        }}
                        className="dropdown-item"
                      >
                        <Plus size={16} className="dropdown-icon" />
                        Add Subtask
                      </button>
                    )}

                    {isTopLevel && lists && lists.length > 1 && (
                      <button
                        onClick={() => {
                          setShowMenu(false)
                          setIsMoveModalOpen(true)
                        }}
                        className="dropdown-item"
                      >
                        <MoveVertical size={16} className="dropdown-icon" />
                        Move to List
                      </button>
                    )}

                    <button
                      onClick={() => {
                        setShowMenu(false)
                        handleDeleteTask()
                      }}
                      className="dropdown-item delete"
                    >
                      <Trash size={16} className="dropdown-icon" />
                      Delete
                    </button>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>

      {expanded && task.subtasks && task.subtasks.length > 0 && (
        <div className="subtasks">
          {task.subtasks.map((subtask) => (
            <TaskItem
              key={subtask.id}
              task={subtask}
              onStatusChange={onStatusChange}
              onCreateSubtask={onCreateSubtask}
              depth={depth + 1}
              fetchTasks={fetchTasks}
              lists={lists}
            />
          ))}
        </div>
      )}

      {isMoveModalOpen && (
        <MoveTaskModal
          isOpen={isMoveModalOpen}
          onClose={() => setIsMoveModalOpen(false)}
          onMove={handleMoveTask}
          lists={lists}
          currentListId={task.list_id}
          taskTitle={task.title}
        />
      )}
    </div>
  )
}

export default TaskItem;
