"use client"

import { useState, useEffect } from "react"
import { X } from "lucide-react"
import api from "../services/api"
import "./Modal.css"

const CreateListModal = ({ isOpen, onClose, onCreate }) => {
  const [title, setTitle] = useState("")
  const [isSubmitting, setIsSubmitting] = useState(false)
  const [existingLists, setExistingLists] = useState([])
  const [error, setError] = useState("")

  useEffect(() => {
    // Fetch existing lists to check for duplicates
    if (isOpen) {
      const fetchLists = async () => {
        try {
          const response = await api.get("/api/todos/lists")
          setExistingLists(response.data.map((list) => list.title.toLowerCase()))
        } catch (error) {
          console.error("Failed to fetch lists:", error)
        }
      }

      fetchLists()
    }
  }, [isOpen])

  if (!isOpen) return null

  const validateTitle = (title) => {
    if (!title.trim()) {
      setError("List title cannot be empty")
      return false
    }

    if (existingLists.includes(title.trim().toLowerCase())) {
      setError("A list with this name already exists")
      return false
    }

    setError("")
    return true
  }

  const handleSubmit = async (e) => {
    e.preventDefault()

    if (!validateTitle(title)) return

    setIsSubmitting(true)
    try {
      await onCreate(title)
      setTitle("")
      setError("")
    } catch (error) {
      // Error handling is done in the parent component
    } finally {
      setIsSubmitting(false)
    }
  }

  return (
    <div className="modal-overlay">
      <div className="modal">
        <div className="modal-header">
          <h2>Create New List</h2>
          <button onClick={onClose} className="close-button">
            <X size={20} />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="modal-form">
          <div className="form-group">
            <label htmlFor="listTitle">List Title</label>
            <input
              id="listTitle"
              type="text"
              value={title}
              onChange={(e) => {
                setTitle(e.target.value)
                if (error) validateTitle(e.target.value)
              }}
              placeholder="Enter list title"
              autoFocus
              className={error ? "input-error" : ""}
            />
            {error && <div className="error-message">{error}</div>}
          </div>

          <div className="modal-footer">
            <button type="button" onClick={onClose} className="btn btn-secondary">
              Cancel
            </button>
            <button type="submit" disabled={!title.trim() || isSubmitting} className="btn btn-primary">
              {isSubmitting ? "Creating..." : "Create List"}
            </button>
          </div>
        </form>
      </div>
    </div>
  )
}

export default CreateListModal
