"use client"

import React, { useState } from "react"
import { createList } from "../../services/api"
import "./Todo.css"

const AddListForm = ({ onListAdded, onCancel }) => {
  const [title, setTitle] = useState("")
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState(null)

  const handleSubmit = async (e) => {
    e.preventDefault()

    if (!title.trim()) {
      setError("Title is required")
      return
    }

    try {
      setLoading(true)
      setError(null)

      const response = await createList(title)

      if (!response.success) {
        throw new Error(response.error)
      }

      const newList = {
        id: response.data.id,
        title,
      }

      onListAdded(newList)
      setTitle("")
    } catch (err) {
      setError(`Failed to add list: ${err.message || 'Unknown error'}`)
      console.error(err)
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className="add-list-form">
      <h3>Create New List</h3>

      {error && <div className="error-message">{error}</div>}

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="list-title">List Title</label>
          <input
            type="text"
            id="list-title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Enter list title"
            required
          />
        </div>

        <div className="form-actions">
          <button type="button" className="cancel-button" onClick={onCancel}>
            Cancel
          </button>
          <button type="submit" className="submit-button" disabled={loading}>
            {loading ? "Creating..." : "Create List"}
          </button>
        </div>
      </form>
    </div>
  )
}

const TodoList = ({ list }) => {
  const [items, setItems] = useState(list.items || []);
  const [newItemContent, setNewItemContent] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleAddItem = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    if (!newItemContent.trim()) {
      setError('Item content is required');
      setLoading(false);
      return;
    }

    try {
      const response = await createItem(list.id, newItemContent);

      if (!response.success) {
        throw new Error(response.error);
      }

      const newItem = {
        id: response.data.id,
        content: newItemContent,
      };

      setItems([...items, newItem]);
      setNewItemContent('');
    } catch (err) {
      setError(`Failed to add item: ${err.message || 'Unknown error'}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="todo-list">
      <h4>{list.title}</h4>
      {error && <div className="error-message">{error}</div>}
      <ul>
        {items.map(item => (
          <li key={item.id}>{item.content}</li>
        ))}
      </ul>
      <form onSubmit={handleAddItem}>
        <input
          type="text"
          value={newItemContent}
          onChange={(e) => setNewItemContent(e.target.value)}
          placeholder="New item"
          required
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Adding...' : 'Add Item'}
        </button>
      </form>
    </div>
  );
};

export default AddListForm;
