"use client"

import { useState, useEffect, useContext } from "react"
import { useNavigate } from "react-router-dom"
import { AuthContext } from "./context/AuthContext"
import api from "../services/api"
import { toast } from "react-toastify"
import TodoList from "../components/TodoList"
import CreateListModal from "../components/CreateListModal"
import { Plus, LogOut } from "lucide-react"

const Dashboard = () => {
  const { user, logout } = useContext(AuthContext)
  const [lists, setLists] = useState([])
  const [isLoading, setIsLoading] = useState(true)
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false)
  const [activeListId, setActiveListId] = useState(null)
  const navigate = useNavigate()

  useEffect(() => {
    if (!user) {
      navigate("/login")
      return
    }

    fetchLists()
  }, [user, navigate])

  const fetchLists = async () => {
    try {
      setIsLoading(true)
      const response = await api.get("/api/todos/lists")
      setLists(response.data)

      // Set the first list as active if there are lists and no active list
      if (response.data.length > 0 && !activeListId) {
        setActiveListId(response.data[0].id)
      }
    } catch (error) {
      toast.error("Failed to fetch todo lists")
    } finally {
      setIsLoading(false)
    }
  }

  const handleCreateList = async (title) => {
    try {
      const response = await api.post("/api/todos/list", { title })
      toast.success("List created successfully")
      fetchLists()
      setIsCreateModalOpen(false)
    } catch (error) {
      toast.error("Failed to create list")
    }
  }

  const handleLogout = async () => {
    const result = await logout()
    if (result.success) {
      navigate("/login")
    } else {
      toast.error(result.message)
    }
  }

  const activeList = lists.find((list) => list.id === activeListId)

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col">
      {/* Header */}
      <header className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4 flex justify-between items-center">
          <h1 className="text-2xl font-bold text-gray-800">Hierarchical Todo</h1>

          <div className="flex items-center space-x-4">
            <span className="text-gray-600">Welcome, {user?.username}</span>
            <button
              onClick={handleLogout}
              className="flex items-center text-gray-700 hover:text-red-600 transition-colors"
            >
              <LogOut size={18} className="mr-1" />
              Logout
            </button>
          </div>
        </div>
      </header>

      {/* Main content */}
      <div className="flex-1 flex">
        {/* Sidebar */}
        <aside className="w-64 bg-white shadow-md">
          <div className="p-4 border-b">
            <button
              onClick={() => setIsCreateModalOpen(true)}
              className="w-full flex items-center justify-center py-2 px-4 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors"
            >
              <Plus size={18} className="mr-1" />
              New List
            </button>
          </div>

          <nav className="p-2">
            <h2 className="text-xs font-semibold text-gray-500 uppercase tracking-wider mb-2 px-2">Your Lists</h2>

            {isLoading ? (
              <div className="flex justify-center p-4">
                <div className="animate-spin rounded-full h-6 w-6 border-t-2 border-b-2 border-gray-900"></div>
              </div>
            ) : lists.length === 0 ? (
              <p className="text-gray-500 text-sm p-2">No lists yet. Create one!</p>
            ) : (
              <ul className="space-y-1">
                {lists.map((list) => (
                  <li key={list.id}>
                    <button
                      onClick={() => setActiveListId(list.id)}
                      className={`w-full text-left px-3 py-2 rounded-md ${
                        activeListId === list.id ? "bg-blue-100 text-blue-700" : "text-gray-700 hover:bg-gray-100"
                      }`}
                    >
                      {list.title}
                    </button>
                  </li>
                ))}
              </ul>
            )}
          </nav>
        </aside>

        {/* Main content area */}
        <main className="flex-1 p-6">
          {activeList ? (
            <TodoList listId={activeListId} listTitle={activeList.title} />
          ) : (
            <div className="flex flex-col items-center justify-center h-full text-gray-500">
              {lists.length === 0 ? (
                <>
                  <p className="mb-4">You don't have any lists yet.</p>
                  <button
                    onClick={() => setIsCreateModalOpen(true)}
                    className="flex items-center py-2 px-4 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors"
                  >
                    <Plus size={18} className="mr-1" />
                    Create Your First List
                  </button>
                </>
              ) : (
                <p>Select a list from the sidebar</p>
              )}
            </div>
          )}
        </main>
      </div>

      {/* Create List Modal */}
      <CreateListModal
        isOpen={isCreateModalOpen}
        onClose={() => setIsCreateModalOpen(false)}
        onCreate={handleCreateList}
      />
    </div>
  )
}

export default Dashboard;
