"use client"

import { useState, useEffect } from "react"
import { ChevronDown, ChevronRight, Plus, Check, MoreVertical, Trash } from "lucide-react"
import api from "../services/api"
import { toast } from "react-toastify"
import "./TaskItem.css"

const TaskItem = ({ task, onStatusChange, onCreateSubtask, depth, fetchTasks }) => {
  const [expanded, setExpanded] = useState(true)
  const [showMenu, setShowMenu] = useState(false)
  const [subtasks, setSubtasks] = useState(task.subtasks || [])

  const maxDepth = 2 // 0-based index, so this is 3 levels
  const canHaveChildren = depth < maxDepth

  useEffect(() => {
    setSubtasks(task.subtasks || [])
  }, [task.subtasks])

  const handleToggleExpand = () => {
    setExpanded(!expanded)
  }

  const handleStatusChange = async () => {
    if (subtasks.length > 0 && subtasks.some(subtask => subtask.status !== "Done")) {
      toast.error("Cannot mark task as done until all subtasks are completed")
      return
    }

    const newStatus = task.status === "Done" ? "Todo" : "Done"
    try {
      await api.patch(`/api/todos/task/${task.id}/status`, { status: newStatus })
      toast.success("Task status updated successfully")
      onStatusChange(task.id, newStatus) // Update the status in the parent component
    } catch (error) {
      toast.error("Failed to update task status")
    }
  }

  const handleSubtaskStatusChange = async (subtaskId, newStatus) => {
    setSubtasks((prevSubtasks) =>
      prevSubtasks.map((st) =>
        st.id === subtaskId ? { ...st, status: newStatus } : st
      )
    )

    if (newStatus === "Done" && subtasks.every(st => st.status === "Done")) {
      await handleStatusChange()
    }
  }

  const handleDeleteTask = async () => {
    try {
      await api.delete(`/api/todos/task/${task.id}`)
      toast.success("Task deleted successfully")
      fetchTasks()
    } catch (error) {
      toast.error("Failed to delete task")
    }
  }

  const getStatusClass = (status) => {
    switch (status) {
      case "Done":
        return "status-done"
      case "In Progress":
        return "status-progress"
      default:
        return "status-todo"
    }
  }

  return (
    <div className="task-item" style={{ marginLeft: `${depth * 20}px` }}>
      <div className="task-header">
        <div className="task-expand">
          {subtasks.length > 0 ? (
            <button onClick={handleToggleExpand} className="expand-button">
              {expanded ? <ChevronDown size={18} /> : <ChevronRight size={18} />}
            </button>
          ) : (
            <div className="expand-placeholder"></div>
          )}
        </div>

        <div className="task-content">
          <div className="task-main">
            <div className="task-checkbox-container">
              <button
                onClick={handleStatusChange}
                className={`task-checkbox ${task.status === "Done" ? "checked" : ""}`}
              >
                {task.status === "Done" && <Check size={14} className="check-icon" />}
              </button>

              <div className="task-details">
                <h3 className={`task-title ${task.status === "Done" ? "completed" : ""}`}>{task.title}</h3>
                {task.description && <p className="task-description">{task.description}</p>}
                <div className="task-meta">
                  <span className={`task-status ${getStatusClass(task.status)}`}>{task.status}</span>
                </div>
              </div>
            </div>

            <div className="task-actions">
              <div className="dropdown">
                <button onClick={() => setShowMenu(!showMenu)} className="dropdown-button">
                  <MoreVertical size={18} />
                </button>

                {showMenu && (
                  <div className="dropdown-menu">
                    {canHaveChildren && (
                      <button
                        onClick={() => {
                          setShowMenu(false)
                          onCreateSubtask(task)
                        }}
                        className="dropdown-item"
                      >
                        <Plus size={16} className="dropdown-icon" />
                        Add Subtask
                      </button>
                    )}
                    <button onClick={handleDeleteTask} className="dropdown-item delete">
                      <Trash size={16} className="dropdown-icon" />
                      Delete
                    </button>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>

      {expanded && subtasks.length > 0 && (
        <div className="subtasks">
          {subtasks.map((subtask) => (
            <TaskItem
              key={subtask.id}
              task={subtask}
              onStatusChange={handleSubtaskStatusChange}
              onCreateSubtask={onCreateSubtask}
              depth={depth + 1}
              fetchTasks={fetchTasks}
            />
          ))}
        </div>
      )}
    </div>
  )
}

export default TaskItem;
