"use client"

import { useState, useEffect, useCallback, useRef } from "react"
import { ChevronDown, ChevronRight, MoreVertical, Check, AlertCircle } from "lucide-react"
import api from "../services/api"
import { toast } from "react-toastify"
import "./TaskItem.css"

// Create a separate component for the error state
const TaskItemError = () => (
  <div className="task-item-error">
    <AlertCircle size={16} className="error-icon" />
    <span>Invalid task data</span>
  </div>
)

const TaskItem = ({
  task,
  onStatusChange,
  onCreateSubtask,
  depth = 0,
  fetchTasks,
  listId,
  onMoveTask,
  onReopenParent,
}) => {
  // All hooks must be called at the top level, before any conditional logic
  const [expanded, setExpanded] = useState(true)
  const [showMenu, setShowMenu] = useState(false)
  const [subtasks, setSubtasks] = useState([])
  const [isUpdatingStatus, setIsUpdatingStatus] = useState(false)

  // Use a ref to track if an update is in progress to prevent duplicate calls
  const updateInProgress = useRef(false)
  // Use a ref to track if component is mounted
  const isMounted = useRef(true)
  // Use a ref for the dropdown menu to handle clicks outside
  const menuRef = useRef(null)

  // Check if task is valid - but don't return early!
  const isTaskValid = task && task.id

  // Set isMounted to false when component unmounts
  useEffect(() => {
    isMounted.current = true

    // Add click event listener to close dropdown when clicking outside
    const handleClickOutside = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setShowMenu(false)
      }
    }

    document.addEventListener("mousedown", handleClickOutside)

    return () => {
      isMounted.current = false
      document.removeEventListener("mousedown", handleClickOutside)
    }
  }, [])

  // Update subtasks state when task changes
  useEffect(() => {
    if (isMounted.current && isTaskValid && task.subtasks) {
      setSubtasks(task.subtasks || [])
    }
  }, [isTaskValid, task?.subtasks])

  // These variables are only used if the task is valid
  const maxDepth = 2 // 0-based index, so this is 3 levels
  const canHaveChildren = isTaskValid && depth < maxDepth
  const hasSubtasks = isTaskValid && subtasks && subtasks.length > 0
  const allSubtasksCompleted = hasSubtasks && subtasks.every((st) => st.status === "Done")

  // Handle status change with proper debouncing and state tracking
  const handleStatusChange = useCallback(async () => {
    if (!isTaskValid) return

    // Prevent multiple simultaneous calls
    if (updateInProgress.current || isUpdatingStatus) return

    // If task has subtasks and not all are completed, prevent manual completion
    if (hasSubtasks && !allSubtasksCompleted && task.status !== "Done") {
      toast.error("Complete all subtasks before marking this task as done", {
        position: "bottom-center",
        autoClose: 3000,
      })
      return
    }

    updateInProgress.current = true
    setIsUpdatingStatus(true)

    const newStatus = task.status === "Done" ? "Todo" : "Done"
    try {
      // Make the API call to update the database
      await api.patch(`/api/todos/task/${task.id}/status`, { status: newStatus })

      // Only show toast and update state if component is still mounted
      if (isMounted.current) {
        toast.success(`Task ${newStatus === "Done" ? "completed" : "reopened"}`)

        // If we're reopening this task and it has a parent, notify the parent to reopen too
        if (newStatus === "Todo" && onReopenParent) {
          onReopenParent()
        }

        // Update the parent component's state
        onStatusChange(task.id, newStatus)
      }
    } catch (error) {
      if (isMounted.current) {
        toast.error("Failed to update task status")
        console.error("Status update error:", error)
      }
    } finally {
      if (isMounted.current) {
        setIsUpdatingStatus(false)
      }
      // Add a small delay before allowing new updates
      setTimeout(() => {
        updateInProgress.current = false
      }, 300)
    }
  }, [
    isTaskValid,
    task?.id,
    task?.status,
    onStatusChange,
    hasSubtasks,
    allSubtasksCompleted,
    isUpdatingStatus,
    onReopenParent,
  ])

  // Handle auto-completion of parent tasks
  useEffect(() => {
    let timer

    // Only proceed if task is valid
    if (!isTaskValid) return () => {}

    // Only run this effect if:
    // 1. Task has subtasks
    // 2. Task is not already marked as Done
    // 3. All subtasks are completed
    // 4. We're not currently updating status
    // 5. Component is still mounted
    if (
      task.subtasks &&
      task.subtasks.length > 0 &&
      task.status !== "Done" &&
      task.subtasks.every((st) => st.status === "Done") &&
      !updateInProgress.current &&
      !isUpdatingStatus &&
      isMounted.current
    ) {
      // Set the flag to prevent duplicate calls
      updateInProgress.current = true

      // Call handleStatusChange after a short delay to prevent race conditions
      timer = setTimeout(() => {
        if (isMounted.current) {
          handleStatusChange()
        }
      }, 100)
    }

    // Clean up the timer if the component unmounts or dependencies change
    return () => {
      if (timer) clearTimeout(timer)
    }
  }, [isTaskValid, task?.subtasks, task?.status, handleStatusChange, isUpdatingStatus])

  const handleToggleExpand = () => {
    setExpanded(!expanded)
  }

  const handleSubtaskStatusChange = async (subtaskId, newStatus) => {
    if (!isTaskValid) return

    // Update local state immediately for better UX
    if (isMounted.current) {
      setSubtasks((prevSubtasks) => prevSubtasks.map((st) => (st.id === subtaskId ? { ...st, status: newStatus } : st)))
    }

    // If a subtask is being reopened and this task is marked as Done,
    // we need to reopen this task as well
    if (newStatus === "Todo" && task.status === "Done") {
      // Wait a bit to avoid race conditions
      setTimeout(() => {
        if (isMounted.current && !updateInProgress.current) {
          handleStatusChange()
        }
      }, 100)
    }
  }

  // This function will be passed to child tasks to notify this task when a child is reopened
  const handleReopenParent = useCallback(() => {
    if (!isTaskValid) return

    // If this task is marked as Done, reopen it
    if (task.status === "Done" && !updateInProgress.current && !isUpdatingStatus) {
      handleStatusChange()
    }
  }, [isTaskValid, task?.status, handleStatusChange, isUpdatingStatus])

  const handleDeleteTask = async () => {
    if (!isTaskValid) return

    // Confirm before deleting
    if (!window.confirm(`Are you sure you want to delete "${task.title}"? This cannot be undone.`)) {
      return
    }

    try {
      await api.delete(`/api/todos/task/${task.id}`)
      if (isMounted.current) {
        toast.success("Task deleted successfully")
        fetchTasks()
      }
    } catch (error) {
      if (isMounted.current) {
        toast.error("Failed to delete task")
        console.error(error)
      }
    }
  }

  const handleMoveTask = () => {
    if (!isTaskValid) return

    onMoveTask(task)
    setShowMenu(false)
  }

  const getStatusClass = (status) => {
    switch (status) {
      case "Done":
        return "status-done"
      case "In Progress":
        return "status-progress"
      default:
        return "status-todo"
    }
  }

  // If task is not valid, render the error component
  if (!isTaskValid) {
    console.error("Invalid task object:", task)
    return <TaskItemError />
  }

  // Determine if the checkbox should be disabled
  const isCheckboxDisabled = isUpdatingStatus || (hasSubtasks && !allSubtasksCompleted && task.status !== "Done")

  // Only render the full component if task is valid
  return (
    <div className="task-item-container">
      <div className={`task-item ${task.status === "Done" ? "completed" : ""}`}>
        <div className="task-header">
          {hasSubtasks ? (
            <button
              className="expand-button"
              onClick={handleToggleExpand}
              aria-label={expanded ? "Collapse subtasks" : "Expand subtasks"}
            >
              {expanded ? <ChevronDown size={18} /> : <ChevronRight size={18} />}
            </button>
          ) : (
            <div className="expand-placeholder"></div>
          )}

          <button
            className={`task-checkbox ${task.status === "Done" ? "checked" : ""} ${isUpdatingStatus ? "updating" : ""}`}
            onClick={handleStatusChange}
            disabled={isCheckboxDisabled}
            aria-label={task.status === "Done" ? "Mark as incomplete" : "Mark as complete"}
            title={isCheckboxDisabled && hasSubtasks ? "Complete all subtasks first" : ""}
            data-task-id={task.id}
          >
            {task.status === "Done" && <Check size={14} className="check-icon" />}
          </button>

          <div className="task-details">
            <h3 className={`task-title ${task.status === "Done" ? "completed" : ""}`}>{task.title}</h3>
            {task.description && <p className="task-description">{task.description}</p>}
            <div className="task-meta">
              <span className={`task-status ${getStatusClass(task.status)}`}>{task.status}</span>
              {hasSubtasks && (
                <span className="task-subtasks-count">
                  {subtasks.filter((st) => st.status === "Done").length}/{subtasks.length} subtasks
                </span>
              )}
            </div>
          </div>

          <div className="task-actions" ref={menuRef}>
            <div className="dropdown">
              <button
                className="dropdown-button"
                onClick={() => setShowMenu(!showMenu)}
                aria-label="Task options"
                aria-expanded={showMenu}
                aria-haspopup="true"
              >
                <MoreVertical size={18} />
              </button>

              {showMenu && (
                <div className="dropdown-menu" role="menu">
                  {canHaveChildren && (
                    <button
                      className="dropdown-item"
                      onClick={() => {
                        onCreateSubtask(task.id, task.title)
                        setShowMenu(false)
                      }}
                      role="menuitem"
                    >
                      Add Subtask
                    </button>
                  )}
                  <button className="dropdown-item" onClick={handleMoveTask} role="menuitem">
                    Move Task
                  </button>
                  <button className="dropdown-item delete" onClick={handleDeleteTask} role="menuitem">
                    Delete
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {expanded && hasSubtasks && (
        <div className="subtasks-container">
          {subtasks.map((subtask) => (
            <TaskItem
              key={subtask.id}
              task={subtask}
              onStatusChange={handleSubtaskStatusChange}
              onCreateSubtask={onCreateSubtask}
              depth={depth + 1}
              fetchTasks={fetchTasks}
              listId={listId}
              onMoveTask={onMoveTask}
              onReopenParent={handleReopenParent}
            />
          ))}
        </div>
      )}
    </div>
  )
}

export default TaskItem;
