"use client"

import { useState, useEffect } from "react"
import { useNavigate } from "react-router-dom"
import { getTodoLists, logoutUser } from "../services/api"
import TodoList from "../components/TodoList"
import AddListForm from "../components/AddListForm"
import MoveTaskModal from "../components/MoveTaskModal"

const Dashboard = () => {
  const [lists, setLists] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  const [activeListId, setActiveListId] = useState(null)
  const [showAddListForm, setShowAddListForm] = useState(false)
  const [moveTaskData, setMoveTaskData] = useState(null)
  const navigate = useNavigate()

  useEffect(() => {
    const fetchLists = async () => {
      try {
        setLoading(true)
        const listsData = await getTodoLists()
        setLists(listsData)

        // Set the first list as active if there are any lists
        if (listsData.length > 0) {
          setActiveListId(listsData[0].id)
        }

        setError(null)
      } catch (err) {
        setError("Failed to load todo lists")
        console.error(err)
      } finally {
        setLoading(false)
      }
    }

    fetchLists()
  }, [])

  const handleLogout = async () => {
    try {
      await logoutUser()
      navigate("/login")
    } catch (error) {
      console.error("Logout failed:", error)
    }
  }

  const handleAddList = (newList) => {
    console.log("handleAddList called with:", newList)
    setLists((prevLists) => {
      console.log("Current lists:", prevLists)
      const updatedLists = [...prevLists, newList]
      console.log("Updated lists:", updatedLists)
      return updatedLists
    })
    setActiveListId(newList.id)
    setShowAddListForm(false)
  }

  const handleMoveTask = (taskId) => {
    setMoveTaskData({
      taskId,
      currentListId: activeListId,
    })
  }

  const executeTaskMove = async (taskId, destinationListId) => {
    // In a real implementation, you would call an API endpoint to move the task
    // For now, we'll just update the UI to simulate the move
    console.log(`Moving task ${taskId} to list ${destinationListId}`)

    // Close the modal
    setMoveTaskData(null)

    // Refresh the current list to reflect the changes
    // In a real implementation, you would refetch the tasks for the current list
  }

  const activeList = lists.find((list) => list.id === activeListId)

  return (
    <div
      className="dashboard-container"
      style={{
        display: "flex",
        height: "100vh",
        overflow: "hidden",
      }}
    >
      {/* Sidebar */}
      <div
        className="sidebar"
        style={{
          width: "250px",
          backgroundColor: "#f5f5f5",
          borderRight: "1px solid #e0e0e0",
          display: "flex",
          flexDirection: "column",
          height: "100%",
        }}
      >
        <div
          className="sidebar-header"
          style={{
            padding: "16px",
            borderBottom: "1px solid #e0e0e0",
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
          }}
        >
          <h1 style={{ margin: 0, fontSize: "1.5rem" }}>Todo Lists</h1>
          <button
            onClick={handleLogout}
            style={{
              background: "none",
              border: "none",
              cursor: "pointer",
              color: "#757575",
              fontSize: "16px",
            }}
            title="Logout"
          >
            ⟲
          </button>
        </div>

        <div
          className="lists-container"
          style={{
            flex: 1,
            overflowY: "auto",
            padding: "16px",
          }}
        >
          {loading ? (
            <div>Loading lists...</div>
          ) : error ? (
            <div className="error-message">{error}</div>
          ) : (
            <>
              <ul
                className="lists-menu"
                style={{
                  listStyle: "none",
                  padding: 0,
                  margin: 0,
                }}
              >
                {lists.map((list) => (
                  <li
                    key={list.id}
                    className={`list-item ${activeListId === list.id ? "active" : ""}`}
                    onClick={() => setActiveListId(list.id)}
                    style={{
                      padding: "12px",
                      borderRadius: "4px",
                      marginBottom: "4px",
                      cursor: "pointer",
                      backgroundColor: activeListId === list.id ? "#e0e0e0" : "transparent",
                    }}
                  >
                    {list.title}
                  </li>
                ))}
              </ul>

              {!showAddListForm && (
                <button
                  onClick={() => setShowAddListForm(true)}
                  style={{
                    display: "flex",
                    alignItems: "center",
                    gap: "8px",
                    background: "none",
                    border: "none",
                    padding: "12px",
                    width: "100%",
                    textAlign: "left",
                    cursor: "pointer",
                    color: "#2196f3",
                    marginTop: "8px",
                  }}
                >
                  + Add New List
                </button>
              )}

              {showAddListForm && (
                <div style={{ marginTop: "16px" }}>
                  <AddListForm onListAdded={handleAddList} />
                  <button
                    onClick={() => setShowAddListForm(false)}
                    style={{
                      background: "none",
                      border: "none",
                      padding: "8px",
                      cursor: "pointer",
                      color: "#757575",
                    }}
                  >
                    Cancel
                  </button>
                </div>
              )}
            </>
          )}
        </div>
      </div>

      {/* Main Content */}
      <div
        className="main-content"
        style={{
          flex: 1,
          padding: "24px",
          overflowY: "auto",
        }}
      >
        {activeList ? (
          <TodoList list={activeList} onMoveTask={handleMoveTask} />
        ) : (
          <div
            className="empty-state"
            style={{
              textAlign: "center",
              marginTop: "64px",
            }}
          >
            {lists.length === 0 ? (
              <>
                <h2>Welcome to Your Todo App!</h2>
                <p>Create your first list to get started.</p>
                <button
                  onClick={() => setShowAddListForm(true)}
                  style={{
                    backgroundColor: "#2196f3",
                    color: "white",
                    border: "none",
                    borderRadius: "4px",
                    padding: "8px 16px",
                    cursor: "pointer",
                    marginTop: "16px",
                  }}
                >
                  Create a List
                </button>
              </>
            ) : (
              <h2>Select a list from the sidebar</h2>
            )}
          </div>
        )}
      </div>

      {/* Move Task Modal */}
      {moveTaskData && (
        <MoveTaskModal
          taskId={moveTaskData.taskId}
          currentListId={moveTaskData.currentListId}
          onMove={executeTaskMove}
          onClose={() => setMoveTaskData(null)}
        />
      )}
    </div>
  )
}

export default Dashboard
