"use client"

import { useState } from "react"
import { updateTaskStatus, createTask } from "../../services/api"
import "./Todo.css"

const TodoItem = ({ task, depth = 0, onStatusChange, onMoveTask, listId, onAddSubtask }) => {
  const [expanded, setExpanded] = useState(true)
  const [showAddForm, setShowAddForm] = useState(false)
  const [showMenu, setShowMenu] = useState(false)

  const hasSubtasks = task.subtasks && task.subtasks.length > 0
  const isCompleted = task.status === "Done"

  const handleToggleExpand = () => {
    setExpanded(!expanded)
  }

  const handleStatusChange = async (newStatus) => {
    try {
      await updateTaskStatus(task.id, newStatus)
      onStatusChange(task.id, newStatus)
    } catch (error) {
      console.error("Failed to update task status:", error)
    }
  }

  const toggleMenu = (e) => {
    e.stopPropagation()
    setShowMenu(!showMenu)
  }

  const handleAddSubtask = () => {
    setShowAddForm(true)
  }

  const handleSubtaskAdded = (newTask) => {
    onAddSubtask(task.id, newTask)
    setShowAddForm(false)
  }

  return (
    <div className="task-container" style={{ marginLeft: `${depth * 20}px` }}>
      <div className={`task-item ${isCompleted ? "completed" : ""}`}>
        <div className="task-header">
          {hasSubtasks ? (
            <button
              onClick={handleToggleExpand}
              className="expand-button"
              aria-label={expanded ? "Collapse" : "Expand"}
            >
              {expanded ? "▼" : "▶"}
            </button>
          ) : (
            <div className="expand-placeholder"></div>
          )}

          <div className="task-title">
            {task.title}
            {task.description && <div className="task-description">{task.description}</div>}
          </div>

          <div className="task-actions">
            {!isCompleted && (
              <button onClick={() => handleStatusChange("Done")} className="complete-button" title="Mark as complete">
                ✓
              </button>
            )}

            {isCompleted && (
              <button onClick={() => handleStatusChange("Todo")} className="undo-button" title="Mark as incomplete">
                ↩
              </button>
            )}

            {depth === 0 && (
              <div className="menu-container">
                <button onClick={toggleMenu} className="menu-button" title="More options">
                  ⋯
                </button>

                {showMenu && (
                  <div className="menu-dropdown">
                    <button
                      onClick={() => {
                        onMoveTask(task.id)
                        setShowMenu(false)
                      }}
                    >
                      Move to another list
                    </button>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      </div>

      {hasSubtasks && expanded && (
        <div className="subtasks">
          {task.subtasks.map((subtask) => (
            <TodoItem
              key={subtask.id}
              task={subtask}
              depth={depth + 1}
              onStatusChange={onStatusChange}
              onMoveTask={onMoveTask}
              listId={listId}
              onAddSubtask={onAddSubtask}
            />
          ))}
        </div>
      )}

      {!isCompleted && depth < 2 && expanded && !showAddForm && (
        <button className="add-subtask-button" onClick={handleAddSubtask}>
          + Add Subtask
        </button>
      )}

      {showAddForm && (
        <div className="add-subtask-form">
          <AddTaskForm
            listId={listId}
            parentId={task.id}
            onTaskAdded={handleSubtaskAdded}
            onCancel={() => setShowAddForm(false)}
          />
        </div>
      )}
    </div>
  )
}

const AddTaskForm = ({ listId, parentId, onTaskAdded, onCancel }) => {
  const [title, setTitle] = useState("")
  const [description, setDescription] = useState("")
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState("")

  const handleSubmit = async (e) => {
    e.preventDefault()

    if (!title.trim()) {
      setError("Title is required")
      return
    }

    try {
      setLoading(true)
      setError("")

      const taskData = {
        title,
        description,
        status: "Todo",
        list_id: listId,
        parent_id: parentId,
      }

      const response = await createTask(taskData)

      const newTask = {
        ...taskData,
        id: response.task_id,
        subtasks: [],
      }

      onTaskAdded(newTask)
    } catch (err) {
      setError("Failed to add task. Please try again.")
      console.error(err)
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className="task-form">
      <h4>{parentId ? "Add Subtask" : "Add Task"}</h4>

      {error && <div className="error-message">{error}</div>}

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="title">Title</label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Enter task title"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="description">Description (optional)</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Enter task description"
          />
        </div>

        <div className="form-actions">
          <button type="button" className="cancel-button" onClick={onCancel}>
            Cancel
          </button>
          <button type="submit" className="submit-button" disabled={loading}>
            {loading ? "Adding..." : "Add Task"}
          </button>
        </div>
      </form>
    </div>
  )
}

export default TodoItem;
