import React, { useState, useEffect } from 'react';
import { getTodoLists, getTasksForList, updateTaskStatus } from '../services/api'; // Ensure correct import

const Dashboard = () => {
  const [lists, setLists] = useState([]);
  const [title, setTitle] = useState('');
  const userId = localStorage.getItem('userId'); // From login

  const fetchLists = async () => {
    try {
      if (userId) {
        const data = await getTodoLists(userId);
        setLists(data);
      }
    } catch (error) {
      console.error('Error fetching lists:', error);
    }
  };

  useEffect(() => {
    fetchLists();
  }, [userId]);

  const handleCreateList = async () => {
    try {
      await createTodoList(title, userId);
      setTitle('');
      fetchLists();
    } catch (error) {
      console.error('Error creating list:', error);
    }
  };

  const handleUpdateTaskStatus = async (taskId, newStatus) => {
    try {
      await updateTaskStatus(taskId, newStatus);
      fetchTasks(); // Refresh tasks after status update
    } catch (error) {
      console.error('Error updating task status:', error);
    }
  };

  return (
    <div style={{ margin: '20px' }}>
      <h2>Dashboard</h2>
      <div>
        <input
          type="text"
          placeholder="New List Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <button onClick={handleCreateList}>Create List</button>
      </div>
      {lists.map((list) => (
        <div key={list.id}>
          <h3>{list.title}</h3>
          <TaskList listId={list.id} onUpdateTaskStatus={handleUpdateTaskStatus} />
        </div>
      ))}
    </div>
  );
};

const TaskList = ({ listId, onUpdateTaskStatus }) => {
  const [tasks, setTasks] = useState([]);

  const fetchTasks = async () => {
    try {
      const taskData = await getTasksForList(listId);
      setTasks(taskData);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, [listId]);

  return (
    <div>
      {tasks.map((task) => (
        <div key={task.id}>
          <p>{task.description}</p>
          <button onClick={() => onUpdateTaskStatus(task.id, 'In Progress')}>Move to In Progress</button>
          <button onClick={() => onUpdateTaskStatus(task.id, 'Done')}>Move to Done</button>
        </div>
      ))}
    </div>
  );
};

export default Dashboard;
