"use client"

import { useState } from "react"
import { createList } from "../services/api"

const AddListForm = ({ onListAdded }) => {
  const [title, setTitle] = useState("")
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState(null)

  const handleSubmit = async (e) => {
    e.preventDefault()

    if (!title.trim()) {
      setError("Title is required")
      return
    }

    try {
      setLoading(true)
      setError(null)

      const response = await createList(title)

      const newList = {
        id: response.list_id,
        title,
      }

      onListAdded(newList)

      // Reset form
      setTitle("")
    } catch (err) {
      setError("Failed to add list. Please try again.")
      console.error(err)
    } finally {
      setLoading(false)
    }
  }

  return (
    <div
      className="add-list-form"
      style={{
        backgroundColor: "#f9f9f9",
        padding: "16px",
        borderRadius: "4px",
        marginBottom: "16px",
      }}
    >
      <h3>Create New List</h3>

      {error && (
        <div
          className="error-message"
          style={{
            color: "#f44336",
            marginBottom: "12px",
          }}
        >
          {error}
        </div>
      )}

      <form onSubmit={handleSubmit} style={{ display: "flex", gap: "8px" }}>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="Enter list title"
          style={{
            flex: 1,
            padding: "8px",
            borderRadius: "4px",
            border: "1px solid #ddd",
          }}
          required
        />

        <button
          type="submit"
          disabled={loading}
          style={{
            backgroundColor: "#2196f3",
            color: "white",
            border: "none",
            borderRadius: "4px",
            padding: "8px 16px",
            cursor: loading ? "not-allowed" : "pointer",
            opacity: loading ? 0.7 : 1,
          }}
        >
          {loading ? "Creating..." : "Create List"}
        </button>
      </form>
    </div>
  )
}

export default AddListForm;
