// src/services/api.js
import axios from 'axios';

// Read the backend URL from .env or default to localhost:5000
const API_URL = process.env.REACT_APP_API_URL || 'http://127.0.0.1:5000';

export const registerUser = async (username, password) => {
  const response = await axios.post(`${API_URL}/api/auth/register`, {
    username,
    password
  });
  return response.data;
};

export const loginUser = async (username, password) => {
  const response = await axios.post(`${API_URL}/api/auth/login`, {
    username,
    password
  });
  return response.data;
};

export const createTodoList = async (title, userId) => {
  const response = await axios.post(`${API_URL}/api/todos/list`, {
    title,
    user_id: userId
  });
  return response.data;
};

export const getTodoLists = async (userId) => {
  const response = await axios.get(`${API_URL}/api/todos/lists/${userId}`);
  return response.data;
};

export const addTask = async (description, listId, parentId = null) => {
  const response = await axios.post(`${API_URL}/api/todos/task`, {
    description,
    list_id: listId,
    parent_id: parentId
  });
  return response.data;
};

