"use client"

import { useState, useEffect } from "react"
import { getTasks, createTask } from "../../services/api"
import TodoItem from "./TodoItem"
import "./Todo.css"

const TodoList = ({ list, onMoveTask }) => {
  const [tasks, setTasks] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  const [showAddForm, setShowAddForm] = useState(false)

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        setLoading(true)
        const tasksData = await getTasks(list.id)
        setTasks(tasksData)
        setError(null)
      } catch (err) {
        setError("Failed to load tasks")
        console.error(err)
      } finally {
        setLoading(false)
      }
    }

    fetchTasks()
  }, [list.id])

  const handleStatusChange = (taskId, newStatus) => {
    // Update tasks recursively
    const updateTasksRecursively = (tasksList) => {
      return tasksList.map((task) => {
        if (task.id === taskId) {
          return { ...task, status: newStatus }
        }
        if (task.subtasks && task.subtasks.length > 0) {
          return {
            ...task,
            subtasks: updateTasksRecursively(task.subtasks),
          }
        }
        return task
      })
    }

    setTasks(updateTasksRecursively(tasks))
  }

  const handleAddTask = (newTask) => {
    // Add the new task to the list
    setTasks([...tasks, { ...newTask, subtasks: [] }])
    setShowAddForm(false)
  }

  const handleAddSubtask = (parentId, newSubtask) => {
    // Add subtask to the correct parent
    const updateTasksWithNewSubtask = (tasksList, parentTaskId) => {
      return tasksList.map((task) => {
        if (task.id === parentTaskId) {
          return {
            ...task,
            subtasks: [...(task.subtasks || []), newSubtask],
          }
        }
        if (task.subtasks && task.subtasks.length > 0) {
          return {
            ...task,
            subtasks: updateTasksWithNewSubtask(task.subtasks, parentTaskId),
          }
        }
        return task
      })
    }

    setTasks(updateTasksWithNewSubtask(tasks, parentId))
  }

  if (loading) return <div className="loading">Loading tasks...</div>
  if (error) return <div className="error-message">{error}</div>

  return (
    <div className="todo-list">
      <div className="list-header">
        <h2>{list.title}</h2>
        <button className="add-task-button" onClick={() => setShowAddForm(!showAddForm)}>
          {showAddForm ? "Cancel" : "+ Add Task"}
        </button>
      </div>

      {showAddForm && (
        <div className="add-task-form-container">
          <AddTaskForm listId={list.id} onTaskAdded={handleAddTask} onCancel={() => setShowAddForm(false)} />
        </div>
      )}

      <div className="tasks-container">
        {tasks.length === 0 ? (
          <div className="empty-state">No tasks yet. Add your first task!</div>
        ) : (
          tasks.map((task) => (
            <TodoItem
              key={task.id}
              task={task}
              onStatusChange={handleStatusChange}
              onMoveTask={onMoveTask}
              listId={list.id}
              onAddSubtask={handleAddSubtask}
            />
          ))
        )}
      </div>
    </div>
  )
}

const AddTaskForm = ({ listId, onTaskAdded, onCancel }) => {
  const [title, setTitle] = useState("")
  const [description, setDescription] = useState("")
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState("")

  const handleSubmit = async (e) => {
    e.preventDefault()

    if (!title.trim()) {
      setError("Title is required")
      return
    }

    try {
      setLoading(true)
      setError("")

      const taskData = {
        title,
        description,
        status: "Todo",
        list_id: listId,
        parent_id: null,
      }

      const response = await createTask(taskData)

      const newTask = {
        ...taskData,
        id: response.task_id,
        subtasks: [],
      }

      onTaskAdded(newTask)
    } catch (err) {
      setError("Failed to add task. Please try again.")
      console.error(err)
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className="task-form">
      <h3>Add New Task</h3>

      {error && <div className="error-message">{error}</div>}

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="title">Title</label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Enter task title"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="description">Description (optional)</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Enter task description"
          />
        </div>

        <div className="form-actions">
          <button type="button" className="cancel-button" onClick={onCancel}>
            Cancel
          </button>
          <button type="submit" className="submit-button" disabled={loading}>
            {loading ? "Adding..." : "Add Task"}
          </button>
        </div>
      </form>
    </div>
  )
}

export default TodoList;
