"use client"

import { useState, useEffect, useContext, useCallback } from "react"
import { useNavigate } from "react-router-dom"
import { AuthContext } from "../context/AuthContext"
import api from "../services/api"
import { toast } from "react-toastify"
import TodoList from "../components/TodoList"
import CreateListModal from "../components/CreateListModal"
import { Plus, LogOut } from "lucide-react"
import "./Dashboard.css"

const Dashboard = () => {
  const { user, logout } = useContext(AuthContext)
  const [lists, setLists] = useState([])
  const [isLoading, setIsLoading] = useState(true)
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false)
  const [activeListId, setActiveListId] = useState(null)
  const navigate = useNavigate()

  // Memoize the fetchLists function with useCallback to prevent infinite loops
  const fetchLists = useCallback(async () => {
    try {
      setIsLoading(true)
      const response = await api.get("/api/todos/lists")
      setLists(response.data)

      // Set the first list as active if there are lists and no active list
      if (response.data.length > 0 && !activeListId) {
        setActiveListId(response.data[0].id)
      }
    } catch (error) {
      toast.error("Failed to fetch todo lists", { autoClose: 5000 })
    } finally {
      setIsLoading(false)
    }
  }, [activeListId])

  useEffect(() => {
    if (!user) {
      navigate("/login")
      return
    }

    fetchLists()
  }, [user, navigate, fetchLists])

  const handleCreateList = async (title) => {
    try {
      // Check if list with this name already exists
      const existingList = lists.find((list) => list.title.toLowerCase() === title.toLowerCase())
      if (existingList) {
        toast.error("A list with this name already exists", { autoClose: 5000 })
        return
      }

      const response = await api.post("/api/todos/list", { title })
      toast.success("List created successfully")
      await fetchLists()

      // Set the newly created list as active
      if (response.data && response.data.list_id) {
        setActiveListId(response.data.list_id)
      }

      setIsCreateModalOpen(false)
    } catch (error) {
      toast.error("Failed to create list", { autoClose: 5000 })
    }
  }

  const handleLogout = async () => {
    const result = await logout()
    if (result.success) {
      navigate("/login")
    } else {
      toast.error(result.message, { autoClose: 5000 })
    }
  }

  const activeList = lists.find((list) => list.id === activeListId)

  return (
    <div className="dashboard">
      {/* Header */}
      <header className="dashboard-header">
        <div className="header-container">
          <h1>Hierarchical Todo</h1>

          <div className="user-controls">
            <span className="welcome-message">Welcome, {user?.username}</span>
            <button onClick={handleLogout} className="logout-button">
              <LogOut size={18} className="icon" />
              Logout
            </button>
          </div>
        </div>
      </header>

      {/* Main content */}
      <div className="dashboard-content">
        {/* Sidebar */}
        <aside className="sidebar">
          <div className="sidebar-header">
            <button onClick={() => setIsCreateModalOpen(true)} className="btn btn-primary new-list-btn">
              <Plus size={18} className="icon" />
              New List
            </button>
          </div>

          <nav className="sidebar-nav">
            <h2 className="sidebar-title">Your Lists</h2>

            {isLoading ? (
              <div className="loading-spinner-container">
                <div className="loading-spinner"></div>
              </div>
            ) : lists.length === 0 ? (
              <p className="empty-message">No lists yet. Create one!</p>
            ) : (
              <ul className="list-menu">
                {lists.map((list) => (
                  <li key={list.id}>
                    <button
                      onClick={() => setActiveListId(list.id)}
                      className={`list-item ${activeListId === list.id ? "active" : ""}`}
                    >
                      {list.title}
                    </button>
                  </li>
                ))}
              </ul>
            )}
          </nav>
        </aside>

        {/* Main content area */}
        <main className="main-content">
          {activeList ? (
            <TodoList listId={activeListId} listTitle={activeList.title} />
          ) : (
            <div className="empty-state">
              {lists.length === 0 ? (
                <>
                  <p>You don't have any lists yet.</p>
                  <button onClick={() => setIsCreateModalOpen(true)} className="btn btn-primary">
                    <Plus size={18} className="icon" />
                    Create Your First List
                  </button>
                </>
              ) : (
                <p>Select a list from the sidebar</p>
              )}
            </div>
          )}
        </main>
      </div>

      {/* Create List Modal */}
      <CreateListModal
        isOpen={isCreateModalOpen}
        onClose={() => setIsCreateModalOpen(false)}
        onCreate={handleCreateList}
      />
    </div>
  )
}

export default Dashboard
