"use client"

import { useState } from "react"
import { createTask } from "../services/api"

const AddTaskForm = ({ listId, parentId = null, onTaskAdded }) => {
  const [title, setTitle] = useState("")
  const [description, setDescription] = useState("")
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState(null)

  const handleSubmit = async (e) => {
    e.preventDefault()

    if (!title.trim()) {
      setError("Title is required")
      return
    }

    try {
      setLoading(true)
      setError(null)

      const taskData = {
        title,
        description,
        status: "Todo",
        list_id: listId,
        parent_id: parentId,
      }

      const response = await createTask(taskData)

      // Add the task ID from the response
      const newTask = {
        ...taskData,
        id: response.task_id,
      }

      onTaskAdded(newTask)

      // Reset form
      setTitle("")
      setDescription("")
    } catch (err) {
      setError("Failed to add task. Please try again.")
      console.error(err)
    } finally {
      setLoading(false)
    }
  }

  return (
    <div
      className="add-task-form"
      style={{
        backgroundColor: "#f9f9f9",
        padding: "16px",
        borderRadius: "4px",
        marginBottom: "16px",
      }}
    >
      <h3>{parentId ? "Add Subtask" : "Add Task"}</h3>

      {error && (
        <div
          className="error-message"
          style={{
            color: "#f44336",
            marginBottom: "12px",
          }}
        >
          {error}
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <div className="form-group" style={{ marginBottom: "12px" }}>
          <label htmlFor="title" style={{ display: "block", marginBottom: "4px" }}>
            Title
          </label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Enter task title"
            style={{
              width: "100%",
              padding: "8px",
              borderRadius: "4px",
              border: "1px solid #ddd",
            }}
            required
          />
        </div>

        <div className="form-group" style={{ marginBottom: "12px" }}>
          <label htmlFor="description" style={{ display: "block", marginBottom: "4px" }}>
            Description (optional)
          </label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Enter task description"
            style={{
              width: "100%",
              padding: "8px",
              borderRadius: "4px",
              border: "1px solid #ddd",
              minHeight: "80px",
            }}
          />
        </div>

        <button
          type="submit"
          disabled={loading}
          style={{
            backgroundColor: "#4caf50",
            color: "white",
            border: "none",
            borderRadius: "4px",
            padding: "8px 16px",
            cursor: loading ? "not-allowed" : "pointer",
            opacity: loading ? 0.7 : 1,
          }}
        >
          {loading ? "Adding..." : "Add Task"}
        </button>
      </form>
    </div>
  )
}

export default AddTaskForm;
