"use client"

import { useState } from "react"
import { ChevronDown, ChevronRight, Plus, Check, MoreVertical, Trash } from "lucide-react"
import api from "../services/api"
import { toast } from "react-toastify"

const TaskItem = ({ task, onStatusChange, onCreateSubtask, depth, fetchTasks }) => {
  const [expanded, setExpanded] = useState(true)
  const [showMenu, setShowMenu] = useState(false)

  const maxDepth = 2 // 0-based index, so this is 3 levels
  const canHaveChildren = depth < maxDepth

  const handleToggleExpand = () => {
    setExpanded(!expanded)
  }

  const handleStatusChange = () => {
    const newStatus = task.status === "Done" ? "Todo" : "Done"
    onStatusChange(task.id, newStatus)
  }

  const handleDeleteTask = async () => {
    try {
      await api.delete(`/api/todos/task/${task.id}`)
      toast.success("Task deleted successfully")
      fetchTasks()
    } catch (error) {
      toast.error("Failed to delete task")
    }
  }

  const getStatusColor = (status) => {
    switch (status) {
      case "Done":
        return "bg-green-100 text-green-800"
      case "In Progress":
        return "bg-yellow-100 text-yellow-800"
      default:
        return "bg-blue-100 text-blue-800"
    }
  }

  return (
    <div className="border rounded-lg shadow-sm bg-white overflow-hidden">
      <div className="p-4 flex items-start">
        <div className="flex-shrink-0 mr-3">
          {task.subtasks && task.subtasks.length > 0 ? (
            <button onClick={handleToggleExpand} className="p-1 rounded-full hover:bg-gray-200">
              {expanded ? <ChevronDown size={18} /> : <ChevronRight size={18} />}
            </button>
          ) : (
            <div className="w-[26px]"></div>
          )}
        </div>

        <div className="flex-1 min-w-0">
          <div className="flex items-start justify-between">
            <div className="flex items-center">
              <button
                onClick={handleStatusChange}
                className={`mr-3 flex-shrink-0 h-5 w-5 rounded border ${
                  task.status === "Done"
                    ? "bg-green-500 border-green-500 flex items-center justify-center"
                    : "border-gray-300"
                }`}
              >
                {task.status === "Done" && <Check size={14} className="text-white" />}
              </button>

              <div>
                <h3
                  className={`text-lg font-medium ${task.status === "Done" ? "line-through text-gray-500" : "text-gray-900"}`}
                >
                  {task.title}
                </h3>
                {task.description && <p className="mt-1 text-sm text-gray-600">{task.description}</p>}
                <div className="mt-2">
                  <span
                    className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getStatusColor(task.status)}`}
                  >
                    {task.status}
                  </span>
                </div>
              </div>
            </div>

            <div className="relative">
              <button onClick={() => setShowMenu(!showMenu)} className="p-1 rounded-full hover:bg-gray-200">
                <MoreVertical size={18} />
              </button>

              {showMenu && (
                <div className="absolute right-0 mt-1 w-48 rounded-md shadow-lg bg-white ring-1 ring-black ring-opacity-5 z-10">
                  <div className="py-1" role="menu" aria-orientation="vertical">
                    {canHaveChildren && (
                      <button
                        onClick={() => {
                          setShowMenu(false)
                          onCreateSubtask(task)
                        }}
                        className="flex items-center px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 w-full text-left"
                        role="menuitem"
                      >
                        <Plus size={16} className="mr-2" />
                        Add Subtask
                      </button>
                    )}
                    <button
                      onClick={handleDeleteTask}
                      className="flex items-center px-4 py-2 text-sm text-red-600 hover:bg-gray-100 w-full text-left"
                      role="menuitem"
                    >
                      <Trash size={16} className="mr-2" />
                      Delete
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {expanded && task.subtasks && task.subtasks.length > 0 && (
        <div className="pl-12 pb-2">
          <div className="space-y-3">
            {task.subtasks.map((subtask) => (
              <TaskItem
                key={subtask.id}
                task={subtask}
                onStatusChange={onStatusChange}
                onCreateSubtask={onCreateSubtask}
                depth={depth + 1}
                fetchTasks={fetchTasks}
              />
            ))}
          </div>
        </div>
      )}
    </div>
  )
}

export default TaskItem;
