"use client"

import { useState, useEffect } from "react"
import { X, MoveVertical } from "lucide-react"
import "./Modal.css"

const MoveTaskModal = ({ isOpen, onClose, onMove, lists, currentListId, taskTitle }) => {
  const [selectedListId, setSelectedListId] = useState("")
  const [isSubmitting, setIsSubmitting] = useState(false)
  const [filteredLists, setFilteredLists] = useState([])

  useEffect(() => {
    if (isOpen && lists) {
      // Filter out the current list
      const filtered = lists.filter((list) => list.id !== currentListId)
      setFilteredLists(filtered)

      // Reset selected list when modal opens
      setSelectedListId("")
    }
  }, [isOpen, lists, currentListId])

  if (!isOpen) return null

  const handleSubmit = async (e) => {
    e.preventDefault()

    if (!selectedListId) return

    setIsSubmitting(true)
    try {
      await onMove(selectedListId)
      // onMove will handle closing the modal and showing success message
    } catch (error) {
      // Error handling is done in the parent component
    } finally {
      setIsSubmitting(false)
    }
  }

  return (
    <div className="modal-overlay">
      <div className="modal">
        <div className="modal-header">
          <h2>Move Task to Another List</h2>
          <button onClick={onClose} className="close-button">
            <X size={20} />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="modal-form">
          <div className="form-group">
            <label>
              <MoveVertical size={16} className="icon" style={{ marginRight: "8px", verticalAlign: "middle" }} />
              Moving: <strong>{taskTitle}</strong>
            </label>
          </div>

          <div className="form-group">
            <label htmlFor="destinationList">Select Destination List</label>
            {filteredLists.length === 0 ? (
              <p className="no-lists-message">No other lists available. Create a new list first.</p>
            ) : (
              <select
                id="destinationList"
                value={selectedListId}
                onChange={(e) => setSelectedListId(e.target.value)}
                className="list-select"
                required
              >
                <option value="">-- Select a list --</option>
                {filteredLists.map((list) => (
                  <option key={list.id} value={list.id}>
                    {list.title}
                  </option>
                ))}
              </select>
            )}
          </div>

          <div className="modal-footer">
            <button type="button" onClick={onClose} className="btn btn-secondary">
              Cancel
            </button>
            <button
              type="submit"
              disabled={!selectedListId || isSubmitting || filteredLists.length === 0}
              className="btn btn-primary"
            >
              {isSubmitting ? "Moving..." : "Move Task"}
            </button>
          </div>
        </form>
      </div>
    </div>
  )
}

export default MoveTaskModal
