"use client"

import { useState, useEffect } from "react"
import { getTodoLists } from "../services/api"

const MoveTaskModal = ({ taskId, currentListId, onMove, onClose }) => {
  const [lists, setLists] = useState([])
  const [selectedListId, setSelectedListId] = useState(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)

  useEffect(() => {
    const fetchLists = async () => {
      try {
        const listsData = await getTodoLists()
        // Filter out the current list
        const otherLists = listsData.filter((list) => list.id !== currentListId)
        setLists(otherLists)
        if (otherLists.length > 0) {
          setSelectedListId(otherLists[0].id)
        }
      } catch (err) {
        setError("Failed to load lists")
        console.error(err)
      } finally {
        setLoading(false)
      }
    }

    fetchLists()
  }, [currentListId])

  const handleMove = () => {
    if (selectedListId) {
      onMove(taskId, selectedListId)
    }
  }

  return (
    <div
      className="modal-overlay"
      style={{
        position: "fixed",
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundColor: "rgba(0, 0, 0, 0.5)",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        zIndex: 1000,
      }}
    >
      <div
        className="modal-content"
        style={{
          backgroundColor: "white",
          padding: "24px",
          borderRadius: "8px",
          width: "400px",
          maxWidth: "90%",
        }}
      >
        <h2 style={{ marginTop: 0 }}>Move Task</h2>

        {loading && <p>Loading lists...</p>}

        {error && <p style={{ color: "red" }}>{error}</p>}

        {!loading && !error && (
          <>
            {lists.length === 0 ? (
              <p>No other lists available to move this task to.</p>
            ) : (
              <>
                <div className="form-group" style={{ marginBottom: "16px" }}>
                  <label htmlFor="list-select" style={{ display: "block", marginBottom: "8px" }}>
                    Select destination list:
                  </label>
                  <select
                    id="list-select"
                    value={selectedListId || ""}
                    onChange={(e) => setSelectedListId(Number(e.target.value))}
                    style={{
                      width: "100%",
                      padding: "8px",
                      borderRadius: "4px",
                      border: "1px solid #ddd",
                    }}
                  >
                    {lists.map((list) => (
                      <option key={list.id} value={list.id}>
                        {list.title}
                      </option>
                    ))}
                  </select>
                </div>

                <div
                  className="button-group"
                  style={{
                    display: "flex",
                    justifyContent: "flex-end",
                    gap: "8px",
                  }}
                >
                  <button
                    onClick={onClose}
                    style={{
                      padding: "8px 16px",
                      borderRadius: "4px",
                      border: "1px solid #ddd",
                      backgroundColor: "#f5f5f5",
                      cursor: "pointer",
                    }}
                  >
                    Cancel
                  </button>
                  <button
                    onClick={handleMove}
                    style={{
                      padding: "8px 16px",
                      borderRadius: "4px",
                      border: "none",
                      backgroundColor: "#2196f3",
                      color: "white",
                      cursor: "pointer",
                    }}
                  >
                    Move
                  </button>
                </div>
              </>
            )}
          </>
        )}
      </div>
    </div>
  )
}

export default MoveTaskModal;
