"use client"

import { useState, useContext, useEffect } from "react"
import { Link, useNavigate } from "react-router-dom"
import { AuthContext } from "../context/AuthContext"
import { toast } from "react-toastify"
import { UserPlus, LogIn } from "lucide-react"
import "./Login.css"

const Login = () => {
  const [username, setUsername] = useState("")
  const [password, setPassword] = useState("")
  const [isLoading, setIsLoading] = useState(false)
  const [showSignupPrompt, setShowSignupPrompt] = useState(false)
  const [isNewUser, setIsNewUser] = useState(false)
  const { login, user } = useContext(AuthContext)
  const navigate = useNavigate()

  // Check if user came from direct URL (might be a new user)
  useEffect(() => {
    const isDirectNavigation = !document.referrer.includes(window.location.host)
    if (isDirectNavigation) {
      setIsNewUser(true)
    }
  }, [])

  useEffect(() => {
    // Redirect if already logged in
    if (user) {
      navigate("/dashboard")
    }
  }, [user, navigate])

  const handleSubmit = async (e) => {
    e.preventDefault()

    if (!username || !password) {
      toast.error("Please enter both username and password")
      return
    }

    setIsLoading(true)

    try {
      const result = await login(username, password)

      if (result.success) {
        toast.success("Login successful!")
        navigate("/dashboard")
      } else {
        // Check if the error message indicates the user doesn't exist
        if (
          result.message.includes("User not found") ||
          result.message.includes("doesn't exist") ||
          result.message.includes("Invalid username")
        ) {
          setShowSignupPrompt(true)
        } else {
          toast.error(result.message)
        }
      }
    } catch (error) {
      toast.error("An error occurred during login")
    } finally {
      setIsLoading(false)
    }
  }

  const handleClosePrompt = () => {
    setShowSignupPrompt(false)
  }

  const handleNavigateToSignup = () => {
    navigate("/register")
  }

  return (
    <div className="auth-container">
      <div className="auth-card">
        <div className="auth-header">
          <h1>Welcome Back</h1>
          <p>Sign in to your account</p>
        </div>

        {isNewUser && !showSignupPrompt && (
          <div className="new-user-banner">
            <div className="new-user-icon">
              <UserPlus size={20} />
            </div>
            <p>
              New to Hierarchical Todo?{" "}
              <Link to="/register" className="auth-link">
                Create an account
              </Link>{" "}
              to get started.
            </p>
          </div>
        )}

        <form onSubmit={handleSubmit} className="auth-form">
          <div className="form-group">
            <label htmlFor="username">Username</label>
            <input
              id="username"
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="Enter your username"
              autoComplete="username"
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              id="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              autoComplete="current-password"
            />
          </div>

          <button type="submit" disabled={isLoading} className="btn btn-primary login-btn">
            {isLoading ? (
              <>
                <div className="btn-spinner"></div>
                Signing in...
              </>
            ) : (
              <>
                <LogIn size={18} className="btn-icon" />
                Sign In
              </>
            )}
          </button>
        </form>

        <div className="auth-footer">
          <p>
            Don't have an account?{" "}
            <Link to="/register" className="auth-link">
              Sign up
            </Link>
          </p>
        </div>
      </div>

      {/* Signup Prompt Modal */}
      {showSignupPrompt && (
        <div className="modal-overlay">
          <div className="modal signup-prompt-modal">
            <div className="modal-header">
              <h2>Account Not Found</h2>
            </div>
            <div className="modal-form">
              <div className="signup-prompt-content">
                <div className="signup-prompt-icon">
                  <UserPlus size={48} />
                </div>
                <p>We couldn't find an account with that username. Would you like to create a new account?</p>
              </div>
              <div className="modal-footer">
                <button onClick={handleClosePrompt} className="btn btn-secondary">
                  Try Again
                </button>
                <button onClick={handleNavigateToSignup} className="btn btn-primary">
                  Sign Up
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  )
}

export default Login;
