"use client"

import { useState } from "react"
import AddTaskForm from "./AddTaskForm"

const AddSubtaskButton = ({ parentTask, listId, onTaskAdded }) => {
  const [showForm, setShowForm] = useState(false)

  const handleTaskAdded = (newTask) => {
    onTaskAdded(newTask)
    setShowForm(false)
  }

  return (
    <div
      className="add-subtask-container"
      style={{
        marginLeft: "24px",
        marginTop: "4px",
        marginBottom: "8px",
      }}
    >
      {showForm ? (
        <div>
          <AddTaskForm listId={listId} parentId={parentTask.id} onTaskAdded={handleTaskAdded} />
          <button
            onClick={() => setShowForm(false)}
            style={{
              background: "none",
              border: "none",
              padding: "8px",
              cursor: "pointer",
              color: "#757575",
            }}
          >
            Cancel
          </button>
        </div>
      ) : (
        <button
          onClick={() => setShowForm(true)}
          style={{
            display: "flex",
            alignItems: "center",
            gap: "8px",
            background: "none",
            border: "none",
            padding: "8px",
            cursor: "pointer",
            color: "#757575",
          }}
        >
          + Add Subtask
        </button>
      )}
    </div>
  )
}

export default AddSubtaskButton;
