import axios from "axios"

// Create axios instance with base URL and credentials
const api = axios.create({
  baseURL: "http://localhost:5001/api",
  withCredentials: true,
})

// Add request interceptor to handle errors
api.interceptors.request.use(
  (config) => {
    // Check if the backend is available before making the request
    return config
  },
  (error) => {
    console.error("API Request Error:", error)
    return Promise.reject(error)
  },
)

// Add response interceptor to handle errors
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.code === "ERR_NETWORK") {
      // Handle network errors (e.g., backend not running)
      console.error("Network Error: Backend might not be running")
      return Promise.reject(new Error("Cannot connect to the server. Please ensure the backend is running."))
    }

    if (error.response?.status === 401) {
      // Handle unauthorized access
      localStorage.removeItem("user")
      localStorage.removeItem("token")
      window.location.href = "/login"
      return Promise.reject(new Error("Session expired. Please login again."))
    }

    return Promise.reject(error)
  },
)

// Authentication
export const registerUser = async (username, password) => {
  try {
    const response = await api.post("/auth/register", { username, password })
    return { success: true, data: response.data }
  } catch (error) {
    return {
      success: false,
      error: error.message || "Registration failed",
    }
  }
}

export const loginUser = async (username, password) => {
  try {
    const response = await api.post("/auth/login", { username, password })
    // Store user info in localStorage
    localStorage.setItem("user", JSON.stringify(response.data.user))
    localStorage.setItem("token", response.data.token)
    api.defaults.headers.common["Authorization"] = `Bearer ${response.data.token}`
    return { success: true, data: response.data }
  } catch (error) {
    return {
      success: false,
      error: error.message || "Login failed",
    }
  }
}

export const logoutUser = async () => {
  try {
    await api.post("/auth/logout")
    localStorage.removeItem("user")
    localStorage.removeItem("token")
    delete api.defaults.headers.common["Authorization"]
    return { success: true }
  } catch (error) {
    // Still remove local storage items even if the API call fails
    localStorage.removeItem("user")
    localStorage.removeItem("token")
    delete api.defaults.headers.common["Authorization"]
    return {
      success: false,
      error: error.message || "Logout failed",
    }
  }
}

// Todo Lists
export const getTodoLists = async () => {
  try {
    const response = await api.get("/todos/lists")
    return response.data
  } catch (error) {
    console.error("Error fetching lists:", error)
    throw new Error(error.message || "Failed to load todo lists")
  }
}

export const createList = async (title) => {
  try {
    const response = await api.post("/todos/list", { title })
    return response.data
  } catch (error) {
    console.error("Error creating list:", error)
    throw new Error(error.message || "Failed to create list")
  }
}

// Tasks
export const getTasks = async (listId) => {
  try {
    const response = await api.get(`/todos/tasks/${listId}`)
    return response.data
  } catch (error) {
    console.error("Error fetching tasks:", error)
    throw new Error(error.message || "Failed to load tasks")
  }
}

export const createTask = async (taskData) => {
  try {
    const response = await api.post("/todos/task", taskData)
    return response.data
  } catch (error) {
    console.error("Error creating task:", error)
    throw new Error(error.message || "Failed to create task")
  }
}

export const updateTaskStatus = async (taskId, status) => {
  try {
    const response = await api.patch(`/todos/task/${taskId}/status`, { status })
    return response.data
  } catch (error) {
    console.error("Error updating task status:", error)
    throw new Error(error.message || "Failed to update task status")
  }
}

// Set token on app initialization
const token = localStorage.getItem("token")
if (token) {
  api.defaults.headers.common["Authorization"] = `Bearer ${token}`
}

export default api
