"use client"

import { useState, useEffect, useCallback } from "react"
import api from "../services/api"
import { toast } from "react-toastify"
import TaskItem from "./TaskItem"
import CreateTaskModal from "./CreateTaskModal"
import { Plus, Loader } from "lucide-react"
import "./TodoList.css"

// Motivational quotes to display when list is empty
const MOTIVATIONAL_QUOTES = [
  "The secret of getting ahead is getting started.",
  "Don't wait. The time will never be just right.",
  "Small progress is still progress.",
  "Focus on progress, not perfection.",
  "The best way to predict the future is to create it.",
]

const TodoList = ({ listId, listTitle }) => {
  const [tasks, setTasks] = useState([])
  const [lists, setLists] = useState([])
  const [isLoading, setIsLoading] = useState(true)
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false)
  const [parentTask, setParentTask] = useState(null)
  const [completionStats, setCompletionStats] = useState({ completed: 0, total: 0, percentage: 0 })
  const [motivationalQuote, setMotivationalQuote] = useState("")

  // Select a random motivational quote
  useEffect(() => {
    const randomIndex = Math.floor(Math.random() * MOTIVATIONAL_QUOTES.length)
    setMotivationalQuote(MOTIVATIONAL_QUOTES[randomIndex])
  }, [listId])

  // Fetch all lists for the move task functionality
  const fetchLists = useCallback(async () => {
    try {
      const response = await api.get("/api/todos/lists")
      setLists(response.data)
    } catch (error) {
      console.error("Failed to fetch lists:", error)
    }
  }, [])

  const fetchTasks = useCallback(async () => {
    if (!listId) return

    try {
      setIsLoading(true)
      const response = await api.get(`/api/todos/tasks/${listId}`)
      setTasks(response.data)

      // Calculate completion statistics
      calculateCompletionStats(response.data)
    } catch (error) {
      toast.error("Failed to fetch tasks")
    } finally {
      setIsLoading(false)
    }
  }, [listId])

  // Calculate task completion statistics
  const calculateCompletionStats = (taskList) => {
    let completed = 0
    let total = 0

    const countTasks = (tasks) => {
      tasks.forEach((task) => {
        total++
        if (task.status === "Done") completed++
        if (task.subtasks && task.subtasks.length > 0) {
          countTasks(task.subtasks)
        }
      })
    }

    countTasks(taskList)

    const percentage = total > 0 ? Math.round((completed / total) * 100) : 0
    setCompletionStats({ completed, total, percentage })
  }

  useEffect(() => {
    if (listId) {
      fetchTasks()
      fetchLists()
    }
  }, [listId, fetchTasks, fetchLists])

  const handleCreateTask = async (taskData) => {
    try {
      const payload = {
        title: taskData.title,
        description: taskData.description || "",
        status: "Todo",
        list_id: listId,
        parent_id: parentTask ? parentTask.id : null,
      }

      await api.post("/api/todos/task", payload)
      toast.success("Task created successfully")
      fetchTasks()
      setIsCreateModalOpen(false)
      setParentTask(null)
    } catch (error) {
      const errorMessage = error.response?.data?.error || "Failed to create task"
      toast.error(errorMessage)
    }
  }

  const handleUpdateTaskStatus = async (taskId, newStatus) => {
    // Update the local state first for immediate feedback
    setTasks((prevTasks) => updateTaskStatusInTree(prevTasks, taskId, newStatus))

    // Then fetch the updated tasks to ensure consistency
    await fetchTasks()
  }

  // Helper function to update task status in the nested tree structure
  const updateTaskStatusInTree = (tasks, taskId, newStatus) => {
    return tasks.map((task) => {
      if (task.id === taskId) {
        return { ...task, status: newStatus }
      }

      if (task.subtasks && task.subtasks.length > 0) {
        return {
          ...task,
          subtasks: updateTaskStatusInTree(task.subtasks, taskId, newStatus),
        }
      }

      return task
    })
  }

  const openCreateSubtaskModal = (task) => {
    setParentTask(task)
    setIsCreateModalOpen(true)
  }

  return (
    <div className="todo-list">
      {/* Progress bar */}
      {!isLoading && tasks.length > 0 && (
        <div className="progress-container">
          <div className="progress-bar">
            <div
              className="progress-fill"
              style={{ width: `${completionStats.percentage}%` }}
              aria-valuemin={0}
              aria-valuemax={100}
              aria-valuenow={completionStats.percentage}
              role="progressbar"
              aria-label="Task completion progress"
            ></div>
          </div>
          <div className="progress-stats">
            <span>
              {completionStats.completed} of {completionStats.total} tasks completed
            </span>
            <span>{completionStats.percentage}% done</span>
          </div>
        </div>
      )}

      <div className="todo-list-header">
        <h2 className="todo-list-title">{listTitle}</h2>
        <button
          onClick={() => {
            setParentTask(null)
            setIsCreateModalOpen(true)
          }}
          className="btn btn-primary"
        >
          <Plus size={18} className="icon" />
          Add Task
        </button>
      </div>

      {isLoading ? (
        <div className="loading-container">
          <Loader className="loading-spinner" />
        </div>
      ) : tasks.length === 0 ? (
        <div className="empty-tasks">
          <p>No tasks in this list yet.</p>
          <button onClick={() => setIsCreateModalOpen(true)} className="btn btn-primary">
            <Plus size={18} className="icon" />
            Create Your First Task
          </button>
          <div className="motivational-quote">"{motivationalQuote}"</div>
        </div>
      ) : (
        <div className="tasks-container">
          {tasks.map((task) => (
            <TaskItem
              key={task.id}
              task={task}
              onStatusChange={handleUpdateTaskStatus}
              onCreateSubtask={openCreateSubtaskModal}
              depth={0}
              fetchTasks={fetchTasks}
              lists={lists}
            />
          ))}
        </div>
      )}

      <CreateTaskModal
        isOpen={isCreateModalOpen}
        onClose={() => {
          setIsCreateModalOpen(false)
          setParentTask(null)
        }}
        onCreate={handleCreateTask}
        isSubtask={!!parentTask}
        parentTaskTitle={parentTask?.title}
      />
    </div>
  )
}

export default TodoList;
