"use client"

import { useState, useEffect } from "react"
import { X } from "lucide-react"
import api from "../services/api"
import "./Modal.css"

const CreateTaskModal = ({ isOpen, onClose, onCreate, isSubtask, parentTaskTitle, listId }) => {
  const [title, setTitle] = useState("")
  const [description, setDescription] = useState("")
  const [isSubmitting, setIsSubmitting] = useState(false)
  const [existingTasks, setExistingTasks] = useState([])
  const [error, setError] = useState("")

  useEffect(() => {
    // Fetch existing tasks to check for duplicates within the same list/parent
    if (isOpen && listId) {
      const fetchTasks = async () => {
        try {
          const response = await api.get(`/api/todos/tasks/${listId}`)
          // Extract task titles at the current level (top-level or under the same parent)
          const tasks = response.data
          const relevantTasks = isSubtask ? tasks.find((t) => t.title === parentTaskTitle)?.subtasks || [] : tasks

          setExistingTasks(relevantTasks.map((task) => task.title.toLowerCase()))
        } catch (error) {
          console.error("Failed to fetch tasks:", error)
        }
      }

      fetchTasks()
    }
  }, [isOpen, listId, isSubtask, parentTaskTitle])

  if (!isOpen) return null

  const validateTitle = (title) => {
    if (!title.trim()) {
      setError("Task title cannot be empty")
      return false
    }

    if (existingTasks.includes(title.trim().toLowerCase())) {
      setError(`A ${isSubtask ? "subtask" : "task"} with this name already exists`)
      return false
    }

    setError("")
    return true
  }

  const handleSubmit = async (e) => {
    e.preventDefault()

    if (!validateTitle(title)) return

    setIsSubmitting(true)
    try {
      await onCreate({ title, description })
      setTitle("")
      setDescription("")
      setError("")
    } catch (error) {
      // Error handling is done in the parent component
    } finally {
      setIsSubmitting(false)
    }
  }

  return (
    <div className="modal-overlay">
      <div className="modal">
        <div className="modal-header">
          <h2>{isSubtask ? `Add Subtask to "${parentTaskTitle}"` : "Create New Task"}</h2>
          <button onClick={onClose} className="close-button">
            <X size={20} />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="modal-form">
          <div className="form-group">
            <label htmlFor="taskTitle">Task Title</label>
            <input
              id="taskTitle"
              type="text"
              value={title}
              onChange={(e) => {
                setTitle(e.target.value)
                if (error) validateTitle(e.target.value)
              }}
              placeholder="Enter task title"
              autoFocus
              className={error ? "input-error" : ""}
            />
            {error && <div className="error-message">{error}</div>}
          </div>

          <div className="form-group">
            <label htmlFor="taskDescription">Description (Optional)</label>
            <textarea
              id="taskDescription"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="Enter task description"
              rows={3}
            />
          </div>

          <div className="modal-footer">
            <button type="button" onClick={onClose} className="btn btn-secondary">
              Cancel
            </button>
            <button type="submit" disabled={!title.trim() || isSubmitting} className="btn btn-primary">
              {isSubmitting ? "Creating..." : "Create Task"}
            </button>
          </div>
        </form>
      </div>
    </div>
  )
}

export default CreateTaskModal;
