"use client"

import { useState, useEffect } from "react"
import { Plus, Loader } from "lucide-react"
import TaskItem from "./TaskItem"
import CreateTaskModal from "./CreateTaskModal"
import MoveTaskModal from "./MoveTaskModal"
import api from "../services/api"
import { toast, ToastContainer } from "react-toastify"
import "react-toastify/dist/ReactToastify.css"
import "./TodoList.css"

const TodoList = ({ list, lists }) => {
  const [tasks, setTasks] = useState([])
  const [loading, setLoading] = useState(true)
  const [showCreateModal, setShowCreateModal] = useState(false)
  const [showMoveModal, setShowMoveModal] = useState(false)
  const [selectedTask, setSelectedTask] = useState(null)
  const [parentTaskId, setParentTaskId] = useState(null)
  const [parentTaskTitle, setParentTaskTitle] = useState("")
  const [completedTasksCount, setCompletedTasksCount] = useState(0)
  const [totalTasksCount, setTotalTasksCount] = useState(0)

  const fetchTasks = async () => {
    try {
      setLoading(true)
      const response = await api.get(`/api/todos/tasks/${list.id}`)

      // Map backend response to our frontend structure
      const mappedTasks = response.data.map((task) => ({
        id: task.id,
        title: task.title,
        description: task.description || "",
        status: task.status,
        subtasks: task.subtasks || [],
      }))

      setTasks(mappedTasks)

      // Calculate task statistics
      const countTasksAndCompleted = (taskList) => {
        let total = 0
        let completed = 0

        const countRecursive = (items) => {
          items.forEach((item) => {
            total++
            if (item.status === "Done") completed++

            if (item.subtasks && item.subtasks.length > 0) {
              countRecursive(item.subtasks)
            }
          })
        }

        countRecursive(mappedTasks)
        return { total, completed }
      }

      const { total, completed } = countTasksAndCompleted(mappedTasks)
      setTotalTasksCount(total)
      setCompletedTasksCount(completed)
    } catch (error) {
      console.error("Failed to fetch tasks:", error)
      toast.error("Failed to load tasks")
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    if (list && list.id) {
      fetchTasks()
    }
  }, [list])

  const handleCreateTask = async (taskData) => {
    try {
      const payload = {
        title: taskData.title,
        description: taskData.description || "",
        status: "Todo",
        list_id: list.id,
        parent_id: parentTaskId,
      }

      await api.post("/api/todos/task", payload)
      toast.success(`${parentTaskId ? "Subtask" : "Task"} created successfully`)
      fetchTasks()
      return true
    } catch (error) {
      console.error("Failed to create task:", error)
      toast.error(error.response?.data?.error || "Failed to create task")
      throw error
    }
  }

  const handleStatusChange = (taskId, newStatus) => {
    // Update the task status in the local state
    const updateTaskStatus = (taskList) => {
      return taskList.map((task) => {
        if (task.id === taskId) {
          return { ...task, status: newStatus }
        }

        if (task.subtasks && task.subtasks.length > 0) {
          return {
            ...task,
            subtasks: updateTaskStatus(task.subtasks),
          }
        }

        return task
      })
    }

    setTasks(updateTaskStatus(tasks))

    // Recalculate task statistics
    const countTasksAndCompleted = (taskList) => {
      let total = 0
      let completed = 0

      const countRecursive = (items) => {
        items.forEach((item) => {
          total++
          if (item.status === "Done") completed++

          if (item.subtasks && item.subtasks.length > 0) {
            countRecursive(item.subtasks)
          }
        })
      }

      countRecursive(taskList)
      return { total, completed }
    }

    const { total, completed } = countTasksAndCompleted(updateTaskStatus(tasks))
    setTotalTasksCount(total)
    setCompletedTasksCount(completed)
  }

  const handleCreateSubtask = (parentId, parentTitle) => {
    setParentTaskId(parentId)
    setParentTaskTitle(parentTitle)
    setShowCreateModal(true)
  }

  const handleMoveTask = (task) => {
    setSelectedTask(task)
    setShowMoveModal(true)
  }

  const getMotivationalQuote = () => {
    const quotes = [
      "The secret of getting ahead is getting started.",
      "Don't wait. The time will never be just right.",
      "Start where you are. Use what you have. Do what you can.",
      "The beginning is always the hardest. Keep going!",
      "Small progress is still progress.",
    ]

    return quotes[Math.floor(Math.random() * quotes.length)]
  }

  const calculateProgress = () => {
    if (totalTasksCount === 0) return 0
    return (completedTasksCount / totalTasksCount) * 100
  }

  return (
    <div className="todo-list">
      <div className="todo-list-header">
        <h2 className="todo-list-title">{list.title}</h2>
        <button
          className="button primary"
          onClick={() => {
            setParentTaskId(null)
            setParentTaskTitle("")
            setShowCreateModal(true)
          }}
        >
          <Plus size={16} />
          <span>Add Task</span>
        </button>
      </div>

      {totalTasksCount > 0 && (
        <div className="progress-container">
          <div className="progress-info">
            <span>
              {completedTasksCount} of {totalTasksCount} tasks completed
            </span>
            <span>{Math.round(calculateProgress())}%</span>
          </div>
          <div className="progress-bar-container">
            <div className="progress-bar-fill" style={{ width: `${calculateProgress()}%` }}></div>
          </div>
        </div>
      )}

      {loading ? (
        <div className="loading-container">
          <Loader className="loading-spinner" />
        </div>
      ) : tasks.length === 0 ? (
        <div className="empty-tasks">
          <p>No tasks yet. Create your first task to get started!</p>
          <button
            className="button primary"
            onClick={() => {
              setParentTaskId(null)
              setParentTaskTitle("")
              setShowCreateModal(true)
            }}
          >
            <Plus size={16} />
            <span>Create Task</span>
          </button>
          <p className="motivational-quote">{getMotivationalQuote()}</p>
        </div>
      ) : (
        <div className="tasks-container">
          {tasks.map((task) => (
            <TaskItem
              key={task.id}
              task={task}
              onStatusChange={handleStatusChange}
              onCreateSubtask={handleCreateSubtask}
              depth={0}
              fetchTasks={fetchTasks}
              listId={list.id}
              onMoveTask={handleMoveTask}
            />
          ))}
        </div>
      )}

      <CreateTaskModal
        isOpen={showCreateModal}
        onClose={() => {
          setShowCreateModal(false)
          setParentTaskId(null)
          setParentTaskTitle("")
        }}
        onCreate={handleCreateTask}
        isSubtask={!!parentTaskId}
        parentTaskTitle={parentTaskTitle}
        listId={list.id}
      />

      {selectedTask && (
        <MoveTaskModal
          isOpen={showMoveModal}
          onClose={() => {
            setShowMoveModal(false)
            setSelectedTask(null)
          }}
          lists={lists}
          currentListId={list.id}
          taskTitle={selectedTask.title}
          task={selectedTask}
          fetchTasks={fetchTasks}
        />
      )}

      <ToastContainer position="bottom-right" />
    </div>
  )
}

export default TodoList
