"use client"

import { useState, useEffect } from "react"
import { useNavigate } from "react-router-dom"
import { getTodoLists, logoutUser, getTasks } from "../services/api"
import TodoList from "../components/todo/TodoList"
import AddListForm from "../components/todo/AddListForm"
import MoveTaskModal from "../components/todo/MoveTaskModal"
import "./Dashboard.css"

const Dashboard = () => {
  const [lists, setLists] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState('')
  const [activeListId, setActiveListId] = useState(null)
  const [showAddListForm, setShowAddListForm] = useState(false)
  const [taskToMove, setTaskToMove] = useState(null)
  const navigate = useNavigate()

  useEffect(() => {
    const fetchLists = async () => {
      const result = await getTodoLists();
      if (result.success) {
        setLists(result.data);
      } else {
        setError(result.error);
      }
      setLoading(false);
    };

    fetchLists();
  }, [])

  const handleLogout = async () => {
    try {
      await logoutUser()
      navigate("/login")
    } catch (error) {
      console.error("Logout failed:", error)
    }
  }

  const handleAddList = (newList) => {
    setLists([...lists, newList])
    setActiveListId(newList.id)
    setShowAddListForm(false)
  }

  const handleMoveTask = (taskId) => {
    const activeList = lists.find((list) => list.id === activeListId)
    if (!activeList) return

    // Find the task in the active list
    const findTask = (tasks, id) => {
      for (const task of tasks) {
        if (task.id === id) return task
        if (task.subtasks && task.subtasks.length > 0) {
          const found = findTask(task.subtasks, id)
          if (found) return found
        }
      }
      return null
    }

    // We need to fetch tasks for the active list first
    const fetchTaskAndShowModal = async () => {
      try {
        const tasks = await getTasks(activeListId)
        const task = findTask(tasks, taskId)
        if (task) {
          setTaskToMove({ task, listId: activeListId })
        }
      } catch (err) {
        console.error("Error fetching task:", err)
      }
    }

    fetchTaskAndShowModal()
  }

  const handleTaskMoved = (taskId, destinationListId) => {
    // In a real app, you would refresh the tasks for the current list
    // For now, we'll just close the modal
    setTaskToMove(null)
  }

  const activeList = lists.find((list) => list.id === activeListId)

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h1>Hierarchical Todo</h1>
        <button className="logout-button" onClick={handleLogout}>
          Logout
        </button>
      </header>

      <div className="dashboard-content">
        <aside className="sidebar">
          <div className="sidebar-header">
            <h2>Your Lists</h2>
            <button className="add-list-button" onClick={() => setShowAddListForm(true)}>
              +
            </button>
          </div>

          {loading ? (
            <div className="loading">Loading lists...</div>
          ) : error ? (
            <div className="error-message">{error}</div>
          ) : (
            <ul className="lists-menu">
              {lists.map((list) => (
                <li
                  key={list.id}
                  className={activeListId === list.id ? "active" : ""}
                  onClick={() => setActiveListId(list.id)}
                >
                  {list.title}
                </li>
              ))}

              {lists.length === 0 && !showAddListForm && (
                <div className="empty-lists">
                  <p>No lists yet</p>
                  <button className="create-list-button" onClick={() => setShowAddListForm(true)}>
                    Create your first list
                  </button>
                </div>
              )}
            </ul>
          )}

          {showAddListForm && (
            <div className="add-list-form-container">
              <AddListForm onListAdded={handleAddList} onCancel={() => setShowAddListForm(false)} />
            </div>
          )}
        </aside>

        <main className="main-content">
          {activeList ? (
            <TodoList list={activeList} onMoveTask={handleMoveTask} />
          ) : (
            <div className="no-list-selected">
              {lists.length > 0 ? <p>Select a list from the sidebar</p> : <p>Create a list to get started</p>}
            </div>
          )}
        </main>
      </div>

      {taskToMove && (
        <MoveTaskModal
          task={taskToMove.task}
          currentListId={taskToMove.listId}
          onClose={() => setTaskToMove(null)}
          onTaskMoved={handleTaskMoved}
        />
      )}
    </div>
  )
}

export default Dashboard;
